package Gradle_Check.patches.projects

import jetbrains.buildServer.configs.kotlin.v2019_2.*
import jetbrains.buildServer.configs.kotlin.v2019_2.Project
import jetbrains.buildServer.configs.kotlin.v2019_2.ui.*

/*
This patch script was generated by TeamCity on settings change in UI.
To apply the patch, change the project with uuid = 'Gradle_Check_Quick_2' (id = 'Gradle_Check_Quick_2')
accordingly, and delete the patch script.
*/
changeProject(uuid("Gradle_Check_Quick_2")) {
    features {
        add {
            feature {
                type = "buildtype-graphs"
                id = "PROJECT_EXT_36"
                param("series", """
                    [
                      {
                        "type": "valueType",
                        "title": "Build Checkout Time",
                        "key": "buildStageDuration:sourcesUpdate"
                      }
                    ]
                """.trimIndent())
                param("format", "text")
                param("title", "Build Checkout Time")
                param("seriesTitle", "Serie")
            }
        }
    }
}
